import React, { useState, useRef } from 'react';
import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import PropsCallback from './components/ItemCount';
import CartWidget from './components/CartWidget';

function App() {
  const [value, setValue] = useState(0);
  const valueRef = useRef(0);

  const handleRefAdd = () => {
    valueRef.current++;
    setValue(valueRef.current);
  };

  const handleRefSubtract = () => {
    if (valueRef.current > 0) {
      valueRef.current--;
      setValue(valueRef.current);
    }
  };

  return (
    <div>
      <NavBar>
        <CartWidget valueRef={valueRef.current} />
      </NavBar>
      <ItemListContainer greetings="Bienvenidos a " greetings1="STADIUM" />
      <PropsCallback
        value={value}
        onRefAdd={handleRefAdd}
        onRefSubtract={handleRefSubtract}
      />
    </div>
  );
}

export default App;
