import Container from "react-bootstrap/Container";
import CardStyle from "./Card";
import data from "../data/data.json";
import { CardWidget } from "./CardWidget";

export const CardList = ({ Datas, Loading, setCartArticle }) => {
	const totalSum = Datas.reduce((accumulator, person) => accumulator + (person.price || 0), 0);

	return (
		<Container className="d-flex flex-wrap mt-3" style={{ justifyContent: 'center', alignItems: 'center' }}>
			{Loading ? (
				<div>Loading...</div>
			) : (
				<>
					<CardWidget total={totalSum} />
					{Datas.map(person => {
						console.log('ApiDatas', person);

						const newImagen = data.filter(dataimg => dataimg.id === person.id);
						person.image = newImagen.length > 0 ? newImagen[0].imagen : person.image;
						console.log('NuevaImagenJson', newImagen);

						return <CardStyle key={person.id} person={person} setCartArticle={setCartArticle} />;
					})}
				</>
			)}
		</Container>
	);
};
