import React, { useState } from "react";
import styled from 'styled-components';

// Container for the whole component
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 1px solid #ddd;
  border-radius: 10px;
  padding: 1rem;
  background-color: #f9f9f9;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  width: 200px; /* Adjust width as needed */
`;

// Container for quantity controls
const QuantityControls = styled.div`
  display: flex;
  align-items: center;
  width: 100%; /* Full width of the container */
  justify-content: space-between; /* Distribute space between buttons */
  margin-bottom: 1rem; /* Space between controls and add button */
`;

// Button styles for quantity control
const Button = styled.button`
  width: 4rem; /* Increased width of buttons */
  height: 4rem; /* Increased height of buttons */
  border: none;
  background-color: #007BFF;
  cursor: pointer;
  color: white;
  font-size: 1.5rem; /* Larger font size for better visibility */
  font-weight: bold; /* Bold font for emphasis */
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: background-color 0.2s ease;

  /* Ensure buttons have equal space */
  margin: 0; /* Remove margin for consistent spacing */

  &:hover {
    background-color: #0056b3;
  }

  &:active {
    background-color: #004085;
  }
`;

// Display for quantity value
const ValueDisplay = styled.span`
  width: 4rem; /* Increased width of value display */
  text-align: center;
  border: none;
  background-color: #f9f9f9;
  color: #333;
  font-size: 1.5rem; /* Larger font size */
  font-weight: bold; /* Bold font for emphasis */
  padding: 0.5rem;
  border-radius: 5px;
  box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.1);
  margin: 0 1rem; /* Space between the value display and buttons */
`;

// Button style for adding to cart
const AddToCartButton = styled.button`
  width: 100%; /* Full width of the container */
  height: 50px; /* Increased height of the button */
  border-radius: 12px;
  border: none;
  background-color: rgb(255, 208, 0);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition-duration: .5s;
  overflow: hidden;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.103);
  position: relative;
  font-size: 1em; /* Font size to match card style */
  font-weight: 600;
  margin-top: 1rem; /* Space between quantity controls and add button */
`;

// Icon container inside the AddToCartButton
const IconContainer = styled.span`
  position: absolute;
  left: 10px; /* Adjusted to position the icon within the button */
  width: 30px;
  height: 30px;
  background-color: transparent;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  z-index: 2;
  transition-duration: .5s;

  svg {
    width: 24px;
    height: 24px;
    fill: rgb(17, 17, 17);
  }
`;

// Text inside the AddToCartButton
const Text = styled.p`
  margin: 0; /* Remove default margin */
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: rgb(17, 17, 17);
  z-index: 1;
  transition-duration: .5s;
`;

const CartButton = styled(AddToCartButton)`
  &:hover ${IconContainer} {
    transform: translateX(58px);
    border-radius: 40px;
    transition-duration: .5s;
  }

  &:hover ${Text} {
    transform: translate(10px,0px);
    transition-duration: .5s;
  }

  &:active {
    transform: scale(0.95);
    transition-duration: .5s;
  }
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(0);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(0); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract}>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd}>+</Button>
      </QuantityControls>
      <CartButton onClick={handleAddToCart}>
        <IconContainer>
          <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 576 512" fill="rgb(17, 17, 17)">
            <path d="M0 24C0 10.7 10.7 0 24 0H69.5c22 0 41.5 12.8 50.6 32h411c26.3 0 45.5 25 38.6 50.4l-41 152.3c-8.5 31.4-37 53.3-69.5 53.3H170.7l5.4 28.5c2.2 11.3 12.1 19.5 23.6 19.5H488c13.3 0 24 10.7 24 24s-10.7 24-24 24H199.7c-34.6 0-64.3-24.6-70.7-58.5L77.4 54.5c-.7-3.8-4-6.5-7.9-6.5H24C10.7 48 0 37.3 0 24zM128 464a48 48 0 1 1 96 0 48 48 0 1 1 -96 0zm336-48a48 48 0 1 1 0 96 48 48 0 1 1 0-96z"></path>
          </svg>
        </IconContainer>
        <Text>Add to Cart</Text>
      </CartButton>
    </Container>
  );
};

export default ItemCount;
