import React, { useState, useRef } from "react";

// Define the ChildCallback component
const ChildCallback = ({ property, text }) => (
  <button onClick={property}>{text}</button>
);

// Define the PropsCallback component with useRef and useState
const PropsCallback = () => {
  const [_, setUpdateOn] = useState(false);

  const refOnAdd = useRef(0);
  const refOnSubstract = useRef(0);

  // Handler to increment the ref value and log it
  const handleRefAdd = () => {
    refOnAdd.current++;
    console.log('refOnAdd:' + refOnAdd.current);
    setUpdateOn(prev => !prev);
  };

  // Handler to decrement the ref value and log it
  const handleRefSubstract = () => {
    refOnSubstract.current--;
    console.log('refOnSubstract:' + refOnSubstract.current);
    setUpdateOn(prev => !prev);
  };

  return (
    <div>
      <ChildCallback property={handleRefSubstract} text='-' />
      <ChildCallback text=' 0 ' />
      <ChildCallback property={handleRefAdd} text='+' />
      <div>
        <p>Value of refOnAdd: {refOnAdd.current}, {refOnSubstract.current}</p>
      </div>
    </div>
  );
};

export default PropsCallback;
