import React from 'react';
import styled from 'styled-components';

const LoaderContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh; /* Altura completa del viewport */
  background-color: #ffffff; /* Fondo blanco para el loader */
`;

const LoaderText = styled.span`
  font-size: 48px;
  display: inline-block;
  font-family: Arial, Helvetica, sans-serif;
  font-weight: bold;
  color: #FFA500; /* Color naranja para el texto */
  letter-spacing: 2px;
  position: relative;
  box-sizing: border-box;

  &::after {
    content: 'Loading';
    position: absolute;
    left: 0;
    top: 0;
    color: #FFA500; /* Color naranja para el texto */
    text-shadow: 0 0 2px #FF4500, 0 0 1px #FF4500, 0 0 1px #FF4500; /* Sombra en tonos más oscuros de naranja */
    width: 100%;
    height: 100%;
    overflow: hidden;
    box-sizing: border-box;
    animation: animloader 6s linear infinite;
  }

  @keyframes animloader {
    0% {
      height: 100%;
    }

    100% {
      height: 0%;
    }
  }
`;

const Loader = () => (
  <LoaderContainer>
    <LoaderText>Loading</LoaderText>
  </LoaderContainer>
);

export default Loader;
