import React, { useState, useRef } from "react";

export const ChildCallback = ({ onClick, text, style }) => (
  <button onClick={onClick} style={style}>
    {text}
  </button>
);

const PropsCallback = ({ value, onAdd, onSubtract }) => {
  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        border: '1px solid black',
        borderRadius: '5px',
      }}>
        <ChildCallback 
          onClick={onSubtract} 
          text='-' 
          style={{
            width: '2rem', 
            border: 'none', 
            backgroundColor: 'white', 
            borderTopLeftRadius: '5px',
            borderBottomLeftRadius: '5px',
            cursor: 'pointer',
            color: 'blue'
          }} 
        />
        <span style={{
          width: '2rem', 
          textAlign: 'center', 
          border: 'none', 
          backgroundColor: 'white', 
          cursor: 'default',
          color: 'orange',
          fontSize: '15px'
        }}>
          {value}
        </span>
        <ChildCallback 
          onClick={onAdd} 
          text='+' 
          style={{
            width: '2rem', 
            border: 'none', 
            backgroundColor: 'white', 
            borderTopRightRadius: '5px',
            borderBottomRightRadius: '5px',
            cursor: 'pointer',
            color: 'blue'
          }} 
        />
      </div>
      <div style={{ marginLeft: '1rem' }}>
        <ChildCallback  
          text='Agregar'
          onClick={() => {}} // Puedes añadir la lógica aquí si es necesaria
          style={{
            backgroundColor: 'lightblue', 
            border: 'none', 
            padding: '0.5rem 1rem'
          }} 
        />
      </div>
    </div>
  );
};

export default PropsCallback;
