import React, { useState, useRef, useEffect } from "react";

const ChildCallback = ({ onClick, text, style }) => (
  <button onClick={onClick} style={style}>
    {text}
  </button>
);

const PropsCallback = ({ setCartArticle }) => {
  const [value, setValue] = useState(0);
  const [isAdded, setIsAdded] = useState(false);
  const valueRef = useRef(0);

  const handleRefAdd = () => {
    valueRef.current++;
    setValue(valueRef.current);
  };

  const handleRefSubtract = () => {
    if (valueRef.current > 0) {
      valueRef.current--;
      setValue(valueRef.current);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setIsAdded(true); // Set isAdded to true to trigger useEffect
  };

  useEffect(() => {
    if (isAdded) {
      // Reset the value and ref after adding to cart
      valueRef.current = 0;
      setValue(0);
      setIsAdded(false); // Reset isAdded to false
    }
  }, [isAdded]); // Dependency array with isAdded

  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        border: '1px solid black',
        borderRadius: '5px',
      }}>
        <ChildCallback 
          onClick={handleRefSubtract} 
          text='-' 
          style={{
            width: '2rem', 
            border: 'none', 
            backgroundColor: 'white', 
            borderTopLeftRadius: '5px',
            borderBottomLeftRadius: '5px',
            cursor: 'pointer',
            color: 'blue'
          }} 
        />
        <span style={{
          width: '2rem', 
          textAlign: 'center', 
          border: 'none', 
          backgroundColor: 'white', 
          cursor: 'default',
          color: 'orange',
          fontSize: '15px'
        }}>
          {value}
        </span>
        <ChildCallback 
          onClick={handleRefAdd} 
          text='+' 
          style={{
            width: '2rem', 
            border: 'none', 
            backgroundColor: 'white', 
            borderTopRightRadius: '5px',
            borderBottomRightRadius: '5px',
            cursor: 'pointer',
            color: 'blue'
          }} 
        />
      </div>
      <div style={{ marginLeft: '1rem' }}>
        <ChildCallback 
          text='Agregar'
          onClick={handleAddToCart} 
          style={{
            backgroundColor: 'lightblue', 
            border: 'none', 
            padding: '0.5rem 1rem'
          }} 
        />
      </div>
    </div>
  );
};

export default PropsCallback;
