// import Button from 'react-bootstrap/Button';
import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import PropsCallback from './PropsCallback'; // Asegúrate de que el nombre y la ruta sean correctos

function CardStyle({ person }) {
  const [cartArticle, setCartArticle] = useState(0); // Maneja el estado del carrito aquí

  return (
    <Card style={{ width: '19rem', padding: '0.5rem', margin: '0.4rem 0.4rem' }}>
      <Card.Img variant="top" src={person.image} />
      <Card.Body>
        <Card.Title>{person.id}</Card.Title>
        <Card.Text>
          Talle: {person.size}<br />
          Color: {person.color}<br />
          Precio: {person.price}
        </Card.Text>
        <PropsCallback setCartArticle={setCartArticle} /> {/* Pasar setCartArticle a PropsCallback */}
        <div style={{ marginTop: '1rem' }}>
          <strong>Artículos en el carrito: {cartArticle}</strong> {/* Muestra el número de artículos en el carrito */}
        </div>
      </Card.Body>
    </Card>
  );
}

export default CardStyle;
