import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for the rotating animation
const rotate = keyframes`
  0% {
    transform: rotate(0deg);
    transform-origin: right bottom;
  }
  25% {
    transform: rotate(90deg);
    transform-origin: right bottom;
  }
  50% {
    transform: rotate(180deg);
    transform-origin: right bottom;
  }
  75% {
    transform: rotate(270deg);
    transform-origin: right bottom;
  }
  100% {
    transform: rotate(360deg);
    transform-origin: right bottom;
  }
`;

// Container for the loader
const LoaderContainer = styled.div`
  width: 150px; /* Increased size */
  height: 175px; /* Increased size */
  margin: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end; /* Align items to the bottom */
  position: relative; /* Needed for absolute positioning */
  top: 50%; /* Center vertically */
  transform: translateY(50%); /* Adjust to move down */
`;

// Title text
const LoadingTitle = styled.label`
  display: block;
  text-align: center;
  font-size: 24px; /* Increased font size */
  font-family: 'Inter', sans-serif;
  font-weight: bold;
  padding-bottom: 20px; /* Adjusted padding */
  color: #888;
`;

// Base loader circle styles
const LoadingCircle = styled.span`
  display: block;
  border-left: 7px solid; /* Increased border width */
  border-top-left-radius: 100%;
  border-top: 7px solid; /* Increased border width */
  margin: 7px; /* Adjusted margin */
  animation: ${rotate} 1500ms linear infinite;
  transform-origin: right bottom;
`;

// Individual spinner circles
const Sp1 = styled(LoadingCircle)`
  border-left-color: #F44336;
  border-top-color: #F44336;
  width: 60px; /* Increased size */
  height: 60px; /* Increased size */
`;

const Sp2 = styled(LoadingCircle)`
  border-left-color: #FFC107;
  border-top-color: #FFC107;
  width: 45px; /* Increased size */
  height: 45px; /* Increased size */
`;

const Sp3 = styled(LoadingCircle)`
  border-left-color: #8bc34a;
  border-top-color: #8bc34a;
  width: 30px; /* Increased size */
  height: 30px; /* Increased size */
`;

const Loader = () => (
  <LoaderContainer>
    <LoadingTitle>Loading ...</LoadingTitle>
    <Sp1>
      <Sp2>
        <Sp3 />
      </Sp2>
    </Sp1>
  </LoaderContainer>
);

export default Loader;
