import React, { useState, useRef, useEffect } from "react";
import styled from 'styled-components';

const CounterContainer = styled.div`
  display: flex;
  align-items: center;
  border: 1px solid black;
  border-radius: 5px;
`;

const Button = styled.button`
  width: 2rem;
  border: none;
  background-color: white;
  cursor: pointer;
  color: blue;

  &:first-child {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
  }

  &:last-child {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
  }
`;

const ValueDisplay = styled.span`
  width: 2rem;
  text-align: center;
  border: none;
  background-color: white;
  cursor: default;
  color: orange;
  font-size: 15px;
`;

const AddToCartButton = styled.button`
  background-color: lightblue;
  border: none;
  padding: 0.5rem 1rem;
  cursor: pointer;
`;

const PropsCallback = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(1);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 1) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(1); // Reset value after adding to cart
  };

  return (
    <CounterContainer>
      <Button onClick={handleRefSubtract}>-</Button>
      <ValueDisplay>{value}</ValueDisplay>
      <Button onClick={handleRefAdd}>+</Button>
      <AddToCartButton onClick={handleAddToCart}>Agregar</AddToCartButton>
    </CounterContainer>
  );
};

export default PropsCallback;
