import React, { useState, useRef } from "react";

// Define the ChildCallback component
const ChildCallback = ({ property, text }) => (
  <button onClick={property}>{text}</button>
);

// Define the PropsCallback component with useRef and useState
const PropsCallback = () => {
  const [value, setValue] = useState(0); // Estado para mostrar el valor

  const refOnAdd = useRef(0);
  const refOnSubstract = useRef(0);

  // Handler to increment the ref value and update the displayed value
  const handleRefAdd = () => {
    refOnAdd.current++;
    setValue(refOnAdd.current - refOnSubstract.current); // Actualiza el valor mostrado
  };

  // Handler to decrement the ref value and update the displayed value
  const handleRefSubstract = () => {
    refOnSubstract.current--;
    setValue(refOnAdd.current - refOnSubstract.current); // Actualiza el valor mostrado
  };

  return (
    <div>
      <ChildCallback property={handleRefSubstract} text='-' />
      <ChildCallback property={() => {}} text={` ${value} `} />
      <ChildCallback property={handleRefAdd} text='+' />
    </div>
  );
};

export default PropsCallback;
