import React, { useState } from "react";
import styled from 'styled-components';

// Container for the whole component
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

// Container for quantity controls
const QuantityControls = styled.div`
  display: flex;
  justify-content: space-between; /* Buttons at the edge */
  align-items: center;
  width: 100%;
  margin-bottom: 1rem;
`;

// Button styles for quantity control
const Button = styled.button`
  width: 50px; /* Adjust width as needed */
  height: 50px; /* Adjust height as needed */
  border: none;
  background-color: #FF6F00; /* Match the orange color */
  cursor: pointer;
  color: white;
  font-size: 1.5rem; /* Increase font size */
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: ${props => (props.roundLeft ? "5px 0 0 5px" : props.roundRight ? "0 5px 5px 0" : "0")}; /* Adjust rounding */

  &:hover {
    background-color: #FF8F00; /* Slightly lighter shade on hover */
  }

  &:active {
    transform: scale(0.95);
  }
`;

// Display for quantity value
const ValueDisplay = styled.span`
  flex-grow: 1;
  height: 50px; /* Adjust height to match buttons */
  text-align: center;
  line-height: 50px; /* Center text vertically */
  border: 2px solid #FF6F00; /* Border matching button color */
  background-color: white;
  color: #FF6F00; /* Orange color for text */
  font-size: 1.5rem; /* Increase font size */
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
`;

// Button style for adding to cart
const AddToCartButton = styled.button`
  width: 100%; /* Full width */
  height: 60px; /* Increase height of the button */
  border-radius: 12px;
  border: 2.5px solid #E0E1E4;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.103);
  cursor: pointer;
  background-color: #FF6F00; /* Orange color */
  transition: all 0.2s ease-in-out;
  font-family: 'Poppins', sans-serif;
  font-size: 1.2rem; /* Increase font size */
  font-weight: bold;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  margin-top: 1rem;
  overflow: hidden; /* Ensure content stays within rounded corners */

  &:hover .IconContainer {
    transform: translateX(58px);
    border-radius: 40px;
    transition-duration: .5s;
  }

  &:hover .text {
    transform: translate(10px, 0px);
    transition-duration: .5s;
  }

  &:active {
    transform: scale(0.95);
    transition-duration: .5s;
  }
`;

const IconContainer = styled.span`
  position: absolute;
  left: 15px; /* Adjust position to align with text */
  width: 30px;
  height: 30px;
  background-color: transparent;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  z-index: 2;
  transition-duration: .5s;
`;

const Text = styled.p`
  height: 100%;
  width: fit-content;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  z-index: 1;
  transition-duration: .5s;
  font-size: 1.2rem; /* Increase font size */
  font-weight: 600;
  margin-left: 40px; /* Adjust spacing between text and icon */
  margin-top: 15px; /* Adjust to move text down */
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(0);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(0); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract} roundLeft>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd} roundRight>+</Button>
      </QuantityControls>
      <AddToCartButton onClick={handleAddToCart}>
        <IconContainer className="IconContainer">
          <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 576 512" fill="white">
            <path d="M0 24C0 10.7 10.7 0 24 0H69.5c22 0 41.5 12.8 50.6 32h411c26.3 0 45.5 25 38.6 50.4l-41 152.3c-8.5 31.4-37 53.3-69.5 53.3H170.7l5.4 28.5c2.2 11.3 12.1 19.5 23.6 19.5H488c13.3 0 24 10.7 24 24s-10.7 24-24 24H199.7c-34.6 0-64.3-24.6-70.7-58.5L77.4 54.5c-.7-3.8-4-6.5-7.9-6.5H24C10.7 48 0 37.3 0 24zM128 464a48 48 0 1 1 96 0 48 48 0 1 1 -96 0zm336-48a48 48 0 1 1 0 96 48 48 0 1 1 0-96z"></path>
          </svg>
        </IconContainer>
        <Text className="text">ADD A TO CAR</Text>
      </AddToCartButton>
    </Container>
  );
};

export default ItemCount;
