import React, { useState, useRef } from "react";
import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import PropsCallback from './components/ItemCount';
import CartWidget from './components/CartWidget'; // Ajusta la ruta según sea necesario

function App() {
  const [value, setValue] = useState(0);
  const valueRef = useRef(0);

  const handleAdd = () => {
    valueRef.current++;
    setValue(valueRef.current);
  };

  const handleSubtract = () => {
    if (valueRef.current > 0) {
      valueRef.current--;
      setValue(valueRef.current);
    }
  };

  return (
    <div>
      <NavBar />
      <ItemListContainer greetings="Bienvenidos a " greetings1="STADIUM" />
      <PropsCallback value={value} onAdd={handleAdd} onSubtract={handleSubtract} />
      <CartWidget value={value} onAdd={handleAdd} onSubtract={handleSubtract} />
    </div>
  );
}

export default App;
