import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function CardStyle({ person }) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh', // Asegura que el div ocupe al menos el alto completo de la ventana
      padding: '1rem' // A単ade un peque単o padding si es necesario
    }}>
      <Card style={{ width: '19rem', padding: '0.5rem', margin: '0.4rem' }}>
        <Card.Img variant="top" src={person.image} />
        <Card.Body>
          <Card.Title>{person.id}</Card.Title>
          <Card.Text>
            Talle: {person.size}<br />
            Color: {person.color}<br />
            Precio: {person.price}
          </Card.Text>
          <Button variant="primary">Click</Button>
        </Card.Body>
      </Card>
    </div>
  );
}

export default CardStyle;
