import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import styled from 'styled-components';

// Contenedor con display flex para la disposición en línea
const DetailContainer = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 20px;
  padding: 20px; /* Añadir un poco de padding alrededor del contenedor */
`;

// Estilo para la imagen con tamaño ajustado
const DetailImage = styled.img`
  max-width: 500px; /* Aumentar el tamaño de la imagen */
  height: auto;
  border-radius: 5px; /* Opcional: para redondear las esquinas */
  object-fit: cover; /* Asegura que la imagen mantenga sus proporciones */
`;

// Estilo para el contenedor de la información
const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 600px; /* Limitar el ancho del contenedor de información */
`;

function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);

  useEffect(() => {
    // Fetch data from JSON file
    fetch('/data.json')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Data fetched:', data); // Verifica los datos obtenidos
        const selectedItem = data.find((item) => item.id === id);
        console.log('Selected item:', selectedItem); // Verifica el ítem seleccionado
        setItem(selectedItem);
      })
      .catch((error) => console.error('Error fetching data:', error));
  }, [id]);

  if (!item) {
    return <div>Cargando detalles...</div>;
  }

  return (
    <div>
      <DetailContainer>
        <DetailImage src={item.imagen} alt={item.marca} />
        <InfoContainer>
          <h2>Descripción</h2>
          <p>
            Reviví el estilo retro de los 80 con los championes Midcity Mid de Adidas:
            <br /><br />
            Estos championes atemporales te permiten moverte con libertad y estilo, combinando a la perfección con cualquier look gracias a su diseño clásico y colores versátiles. La estética vintage no solo te da un toque de nostalgia, sino que también se adapta a las tendencias actuales, haciendo de estos championes una elección ideal para cualquier ocasión.
            <br /><br />
            La comodidad es clave, y con la mediasuela Cloudfoam, te ofrecen una sensación suave y acolchada durante todo el día. Ya sea que estés caminando por la ciudad o pasando tiempo con amigos, disfrutarás de un confort inigualable que mantiene tus pies descansados y frescos. Además, el exterior de estos championes contiene al menos un 50% de materiales reciclados, reflejando el compromiso de Adidas con el medio ambiente y su esfuerzo por reducir los residuos plásticos.
            <br /><br />
            ¡Comprá ahora mismo y dale un toque retro a tu estilo con Adidas!
          </p>
          <h2>Características</h2>
          <p>Marca: {item.marca}</p>
          <p>Género: {item.género}</p>
          <p>Estilo del champión: {item["estilo del champión"]}</p>
          <p>Tipos de ajuste: {item["tipos de ajuste"]}</p>
          <p>Materiales del exterior: {item["materiales del exterior"]}</p>
          <p>¿Con materiales reciclados?: {item["con materiales reciclados?"]}</p>
          <p>Material de la suela: {item["material de la suela"]}</p>
          <p>Materiales del interior: {item["materiales del interior"]}</p>
          <p>Plantilla: {item.plantilla}</p>
        </InfoContainer>
      </DetailContainer>
      <Link to="/">Volver al inicio</Link>
    </div>
  );
}

export default ItemDetail;
