import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import { useEffect, useState, } from 'react';
import { CardList } from './components/CardList';
import data from './data/data.json'

function App() {
  const [cartArticle, setCartArticle] = useState(0)

  const [Datas, setDatas] = useState ([])
  const [Loading, setLoading] = useState (true)

  useEffect (() => {
    new Promise(resolve =>
      setTimeout ((setDatas) => resolve(setDatas), 3000)
    )
    setDatas(data);
    setLoading(false);

    fetch('https://66ac0d49f009b9d5c730fd4f.mockapi.io/stock')
    .then((response) => response.json(data)
  ).then((apiDatas) => setDatas(apiDatas)
).catch((error) => 
  console.error('Error fetching data:', error)
).finally(() => setLoading(false))
  }, [])


  return( 
<div>
    <NavBar cartArticle={cartArticle}/>
    <ItemListContainer greetings ="Bienvenidos a " greetings1="STADIUM" />
    <CardList Datas={Datas} Loading={Loading} setCartArticle={setCartArticle} />
</div>
  );
}

export default App;
