import React, { useState } from "react";
import styled from 'styled-components';

// Container for the whole component
const Container = styled.div`
  display: flex;
  justify-content: space-between; /* Distribute space between items */
  align-items: center;
  border: 1px solid black;
  border-radius: 5px;
  padding: 0.5rem;
  background-color: white;
`;

// Container for quantity controls
const QuantityControls = styled.div`
  display: flex;
  align-items: center;
`;

// Button styles for quantity control
const Button = styled.button`
  width: 2.5rem; /* Width of buttons */
  height: 2.5rem; /* Height of buttons */
  border: none;
  background-color: white;
  cursor: pointer;
  color: blue;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */

  &:first-child {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
  }

  &:last-child {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
  }
`;

// Display for quantity value
const ValueDisplay = styled.span`
  width: 2.5rem; /* Width of value display */
  text-align: center;
  border: none;
  background-color: white;
  cursor: default;
  color: orange;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */
`;

// Button style for adding to cart
const AddToCartButton = styled.button`
  width: 140px;
  height: 40px;
  border-radius: 12px;
  border: none;
  background-color: rgb(255, 208, 0);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition-duration: 0.5s;
  overflow: hidden;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.103);
  position: relative;
`;

const IconContainer = styled.span`
  position: absolute;
  left: -50px;
  width: 30px;
  height: 30px;
  background-color: transparent;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  z-index: 2;
  transition-duration: 0.5s;
`;

const CartIcon = styled.svg`
  width: 1em;
  height: 1em;
  fill: rgb(17, 17, 17);
`;

const ButtonText = styled.p`
  height: 100%;
  width: fit-content;
  display: flex;
  align-items: center;
  justify-content: center;
  color: rgb(17, 17, 17);
  z-index: 1;
  transition-duration: 0.5s;
  font-size: 1.04em;
  font-weight: 600;
`;

const AddToCartButtonStyled = styled(AddToCartButton)`
  &:hover ${IconContainer} {
    transform: translateX(58px);
    border-radius: 40px;
    transition-duration: 0.5s;
  }

  &:hover ${ButtonText} {
    transform: translate(10px, 0px);
    transition-duration: 0.5s;
  }

  &:active {
    transform: scale(0.95);
    transition-duration: 0.5s;
  }
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(0);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(0); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract}>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd}>+</Button>
      </QuantityControls>
      <AddToCartButtonStyled onClick={handleAddToCart}>
        <IconContainer>
          <CartIcon xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512">
            <path d="M0 24C0 10.7 10.7 0 24 0H69.5c22 0 41.5 12.8 50.6 32h411c26.3 0 45.5 25 38.6 50.4l-41 152.3c-8.5 31.4-37 53.3-69.5 53.3H170.7l5.4 28.5c2.2 11.3 12.1 19.5 23.6 19.5H488c13.3 0 24 10.7 24 24s-10.7 24-24 24H199.7c-34.6 0-64.3-24.6-70.7-58.5L77.4 54.5c-.7-3.8-4-6.5-7.9-6.5H24C10.7 48 0 37.3 0 24zM128 464a48 48 0 1 1 96 0 48 48 0 1 1 -96 0zm336-48a48 48 0 1 1 0 96 48 48 0 1 1 0-96z"></path>
          </CartIcon>
        </IconContainer>
        <ButtonText>Add to Cart</ButtonText>
      </AddToCartButtonStyled>
    </Container>
  );
};

export default ItemCount;
