import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import { useEffect, useState } from 'react';
import { CardList } from './components/CardList';
import { CartAndPriceWidget } from './components/CartAndPriceWidget'; // Ajusta la ruta según sea necesario

function App() {
  const [cartArticle, setCartArticle] = useState(0);
  const [Datas, setDatas] = useState([]);
  const [Loading, setLoading] = useState(true);

  useEffect(() => {
    new Promise(resolve =>
      setTimeout(() => resolve(), 3000) // Simula un retraso
    )
    .then(() => fetch('https://66ac0d49f009b9d5c730fd4f.mockapi.io/stock'))
    .then(response => response.json())
    .then(data => setDatas(data))
    .catch(error => console.error('Error fetching data:', error))
    .finally(() => setLoading(false));
  }, []);

  // Calcula la suma total de los precios
  const totalSum = Datas.reduce((accumulator, person) => accumulator + (person.price || 0), 0);

  return (
    <div>
      <NavBar cartArticle={cartArticle} />
      <ItemListContainer greetings="Bienvenidos a " greetings1="STADIUM" />
      <CartAndPriceWidget total={totalSum} cartArticle={cartArticle} />
      <CardList Datas={Datas} Loading={Loading} setCartArticle={setCartArticle} />
    </div>
  );
}

export default App;
