import react from "react"

// Simulación de un servicio asíncrono que devuelve una promesa con o sin errores
function fetchProducts(shouldFail = false) {
    return new Promise((resolve, reject) => {
      // Simulamos un retraso en la respuesta del servicio
      setTimeout(() => {
        if (shouldFail) {
          // Simula un error
          reject(new Error('Error al obtener los productos'));
        } else {
          // Datos de productos simulados
          const products = [
            { id: '1', name: 'Laptop', description: 'Un portátil de alta gama', stock: 10 },
            { id: '2', name: 'Teléfono', description: 'Un teléfono inteligente', stock: 25 },
            { id: '3', name: 'Cámara', description: 'Una cámara digital de 20 MP', stock: 5 }
          ];
          resolve(products);
        }
      }, 2000); // 2 segundos de retraso
    });
  }

  // Uso de la promesa
  fetchProducts(true)  // Pasamos `true` para simular un error
    .then((products) => {
      console.log('Productos:', products);
    })
    .catch((error) => {
      console.error('Error al obtener los productos:', error.message);
    });
