import React, { useState, useRef } from "react";

// Define el componente ChildCallback
const ChildCallback = ({ onClick, text }) => (
  <button onClick={onClick}>{text}</button>
);

// Define el componente PropsCallback con useRef y useState
const PropsCallback = () => {
  const [value, setValue] = useState(0); // Estado para mostrar el valor
  const valueRef = useRef(0); // Referencia para almacenar el valor actual

  // Handler para incrementar el valor de la referencia y actualizar el valor mostrado
  const handleRefAdd = () => {
    valueRef.current++;
    setValue(valueRef.current); // Actualiza el valor mostrado
  };

  // Handler para decrementar el valor de la referencia y actualizar el valor mostrado
  const handleRefSubtract = () => {
    valueRef.current--;
    setValue(valueRef.current); // Actualiza el valor mostrado
  };

  return (
    <div>
      <ChildCallback onClick={handleRefSubtract} text='-' />
      <span>{value}</span>
      <ChildCallback onClick={handleRefAdd} text='+' />
    </div>
  );
};

export default PropsCallback;
