import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { ChildCallback } from './ItemCount'; // Ajusta la ruta según sea necesario

function CartWidget({ value, onAdd, onSubtract }) {
  return (
    <div style={{ 
      position: 'relative',
      display: 'inline-block',
      marginRight: '10px',
    }}>
      <FontAwesomeIcon icon={faShoppingCart} style={{ color: "white" }} />
      <div
        style={{
          position: 'absolute',
          top: '-30%',
          left: '70%',
          transform: 'translate(-50%, -50%)',
          color: 'white',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          fontSize: '12px',
        }}
      >
        <div style={{
          display: 'flex',
          alignItems: 'center',
          border: '1px solid black',
          borderRadius: '5px',
        }}>
          <ChildCallback 
            onClick={onSubtract} 
            text='-' 
            style={{
              width: '2rem', 
              border: 'none', 
              backgroundColor: 'white', 
              borderTopLeftRadius: '5px',
              borderBottomLeftRadius: '5px',
              cursor: 'pointer',
              color: 'blue'
            }} 
          />
          <span style={{
            width: '2rem', 
            textAlign: 'center', 
            border: 'none', 
            backgroundColor: 'white', 
            cursor: 'default',
            color: 'orange',
            fontSize: '15px'
          }}>
            {value}
          </span>
          <ChildCallback 
            onClick={onAdd} 
            text='+' 
            style={{
              width: '2rem', 
              border: 'none', 
              backgroundColor: 'white', 
              borderTopRightRadius: '5px',
              borderBottomRightRadius: '5px',
              cursor: 'pointer',
              color: 'blue'
            }} 
          />
        </div>
      </div>
    </div>
  );
}

export default CartWidget;
