import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import { useEffect, useState } from 'react';
import {CardList} from './components/CardList'; // Asegúrate de que el nombre del archivo sea correcto

function App() {
  const [Datas, setDatas] = useState([]);
  const [Loading, setLoading] = useState(true);
  const [cartArticle, setCartArticle] = useState(0); // Agregar estado para cartArticle

  useEffect(() => {
    // Fetch data from API
    fetch('https://66ac0d49f009b9d5c730fd4f.mockapi.io/stock')
      .then((response) => response.json())
      .then((data) => setDatas(data))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <NavBar cartArticle={cartArticle} /> {/* Pasar cartArticle a NavBar */}
      <ItemListContainer greetings="Bienvenidos a " greetings1="STADIUM" />
      <CardList Datas={Datas} Loading={Loading} setCartArticle={setCartArticle} /> {/* Pasar setCartArticle a CardList */}
    </div>
  );
}

export default App;
