import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import { useEffect, useState } from 'react';
import { CardList } from './components/CardList';
import data from './data/data.json'; // Datos locales

function App() {
  const [cartArticle, setCartArticle] = useState(0);
  const [Datas, setDatas] = useState([]);
  const [Loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulamos una carga inicial con datos locales antes de intentar la API
    setDatas(data);
    setLoading(false);

    // Intentar obtener datos de la API
    fetch('https://66ac0d49f009b9d5c730fd4f.mockapi.io/stock')
      .then((response) => response.json())
      .then((apiData) => setDatas(apiData))
      .catch((error) => {
        console.error('Error fetching data:', error);
        // Si hay error, se mantienen los datos locales
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <NavBar cartArticle={cartArticle} />
      <ItemListContainer greetings="Bienvenidos a " greetings1="STADIUM" />
      <CardList Datas={Datas} Loading={Loading} setCartArticle={setCartArticle} />
    </div>
  );
}

export default App;
