import React from 'react';
import Card from 'react-bootstrap/Card';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';

// Componente que muestra el total de precios y el icono del carrito
export const CartAndPriceWidget = ({ total, cartArticle }) => {
  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      {/* Componente de tarjeta que muestra el total */}
      <Card style={{ width: '18rem', margin: '1rem' }}>
        <Card.Body>
          <Card.Title>Total Price</Card.Title>
          <Card.Text>
            {`Total: $${total.toFixed(2)}`}
          </Card.Text>
        </Card.Body>
      </Card>

      {/* Componente del icono del carrito */}
      <div
        style={{ 
          position: 'relative',
          display: 'inline-block',
          marginRight: '10px',
        }}
      >
        <FontAwesomeIcon icon={faShoppingCart} style={{ color: 'white' }} />
        <div
          style={{
            position: 'absolute',
            top: '-30%',
            left: '70%',
            transform: 'translate(-50%, -50%)',
            color: 'white',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: '12px',
            backgroundColor: 'red', // Cambia el color de fondo para visibilidad
            borderRadius: '50%',
            width: '20px',
            height: '20px',
          }}
        >
          {cartArticle}
        </div>
      </div>
    </div>
  );
};
