import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { useItem } from '../ItemContext';

// Contenedor con display flex para la disposición en línea
const DetailContainer = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 20px;
`;

// Estilo para la imagen con tamaño ajustado
const DetailImage = styled.img`
  max-width: 200px; /* Ajusta este valor según sea necesario */
  border-radius: 5px; /* Opcional: para redondear las esquinas */
`;

// Estilo para el contenedor de la información
const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

function ItemDetail() {
  const { selectedItem } = useItem();

  if (!selectedItem) {
    return <div>Cargando detalles...</div>;
  }

  return (
    <div>
      <h3>Detalle del Artículo</h3>
      <DetailContainer>
        <DetailImage src={selectedItem.image} alt={selectedItem.name} />
        <InfoContainer>
          <p>ID del artículo: {selectedItem.id}</p>
          <p>Nombre: {selectedItem.name}</p>
          <p>Talle: {selectedItem.size}</p>
          <p>Color: {selectedItem.color}</p>
          <p>Precio: {selectedItem.price}</p>
        </InfoContainer>
      </DetailContainer>
      <Link to="/">Volver al inicio</Link>
    </div>
  );
}

export default ItemDetail;
