import React from 'react';
import Card from 'react-bootstrap/Card';
import PropsCallback from './ItemCount';

function CardStyle({ person, setCartArticle }) {
  // Verifica que `person.image` esté definido y sea una ruta válida
  const imageUrl = person.imagen;

  return (
    <Card style={{ width: '19rem', padding: '0.5rem', margin: '0.4rem 0.4rem' }}>
      <Card.Img variant="top" src={imageUrl} alt={person.name} />
      <Card.Body>
        <Card.Title>{person.name}</Card.Title>
        <Card.Text>
          Talle: {person.size}<br />
          Color: {person.color}<br />
          Precio: {person.price}
        </Card.Text>
        <PropsCallback setCartArticle={setCartArticle} />
      </Card.Body>
    </Card>
  );
}

export default CardStyle;
