import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import { useEffect, useState } from 'react';
import { CardList } from './components/CardList';

function App() {
  // Estado para los artículos del carrito
  const [cartItems, setCartItems] = useState([]);

  // Estado para los datos y el estado de carga
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Función para agregar artículos al carrito
  const addToCart = (item, quantity) => {
    setCartItems(prevItems => {
      const existingItemIndex = prevItems.findIndex(cartItem => cartItem.id === item.id);
      if (existingItemIndex > -1) {
        // El artículo existe, actualizar cantidad
        const updatedItems = [...prevItems];
        updatedItems[existingItemIndex].quantity += quantity;
        return updatedItems;
      } else {
        // El artículo no existe, agregar nuevo
        return [...prevItems, { ...item, quantity }];
      }
    });
  };

  // Obtener la cantidad total de artículos en el carrito
  const getTotalItems = () => cartItems.reduce((acc, item) => acc + item.quantity, 0);

  useEffect(() => {
    // Simular un retraso para la carga de datos
    new Promise(resolve => setTimeout(() => resolve(), 3000))
      .then(() => {
        // Cargar datos de la API
        return fetch('https://66ac0d49f009b9d5c730fd4f.mockapi.io/stock');
      })
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching data:', error))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <NavBar cartArticle={getTotalItems()} />
      <ItemListContainer greetings="Bienvenidos a " greetings1="STADIUM" />
      {loading ? (
        <div className="loader-container">
          <span className="loader">Loading</span>
        </div>
      ) : (
        <CardList data={data} loading={loading} addToCart={addToCart} />
      )}
    </div>
  );
}

export default App;
