import React, { useState, useRef } from "react";

const ChildCallback = ({ onClick, text, style }) => (
  <button onClick={onClick} style={style}>
    {text}
  </button>
);

export const ChildCallback1 = ({text, style, onClick}) => (
    <mark onClick={onClick} style={style}>
        {text}
    </mark>
)
const PropsCallback = () => {
  const [value, setValue] = useState(0);

  const valueRef = useRef(0);

  const handleRefAdd = () => {
    valueRef.current++;
    setValue(valueRef.current);
  };

  const handleRefSubtract = () => {
    if (valueRef.current > 0) {
      valueRef.current--;
      setValue(valueRef.current);
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        border: '1px solid black',
        borderRadius: '5px',
      }}>
        <ChildCallback 
          onClick={handleRefSubtract} 
          text='-' 
          style={{
            width: '2rem', 
            border: 'none', 
            backgroundColor: 'white', 
            borderTopLeftRadius: '5px',
            borderBottomLeftRadius: '5px',
            cursor: 'pointer',
            color: 'blue'
          }} 
        />
        <span style={{
          width: '2rem', 
          textAlign: 'center', 
          border: 'none', 
          backgroundColor: 'white', 
          cursor: 'default',
          color: 'orange',
          fontSize : '15px'
        }}>
          {value}
        </span>
        <ChildCallback 
          onClick={handleRefAdd} 
          text='+' 
          style={{
            width: '2rem', 
            border: 'none', 
            backgroundColor: 'white', 
            borderTopRightRadius: '5px',
            borderBottomRightRadius: '5px',
            cursor: 'pointer',
            color: 'blue'
          }} 
        />
      </div>
      <div style={{ marginLeft: '1rem' }}>
        <ChildCallback1  
          text='Agregar'
          onClick={() => setValue(prev => !prev)} 
          style={{
            backgroundColor: 'lightblue', 
            border: 'none', 
            padding: '0.5rem 1rem'
          }} 
        />
        <h3 >
            valueRef.current: {valueRef.current}</h3>
      </div>
    </div>
  );
};

export default PropsCallback;
