import React, { useState } from "react";
import styled from 'styled-components';

// Container for the whole component
const Container = styled.div`
  display: flex;
  flex-direction: column; /* Stack items vertically */
  align-items: center;
  border: 1px solid #ddd;
  border-radius: 10px;
  padding: 1rem;
  background-color: #f9f9f9;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  width: 200px; /* Adjust width as needed */
`;

// Container for quantity controls
const QuantityControls = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem; /* Space between controls and add button */
`;

// Button styles for quantity control
const Button = styled.button`
  width: 2.5rem; /* Width of buttons */
  height: 2.5rem; /* Height of buttons */
  border: none;
  background-color: #007BFF;
  cursor: pointer;
  color: white;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #0056b3;
  }

  &:active {
    background-color: #004085;
  }
`;

// Display for quantity value
const ValueDisplay = styled.span`
  width: 2.5rem; /* Width of value display */
  text-align: center;
  border: none;
  background-color: #f9f9f9;
  color: #333;
  font-size: 1.5rem; /* Larger font size */
  font-weight: bold; /* Bold font for emphasis */
  padding: 0.5rem;
  border-radius: 5px;
  box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.1);
`;

// Button style for adding to cart
const AddToCartButton = styled.button`
  position: relative;
  width: 150px;
  height: 40px;
  cursor: pointer;
  display: flex;
  align-items: center;
  border: 1px solid #34974d;
  background-color: #3aa856;
  transition: all 0.3s;

  &:hover {
    background: #34974d;
  }

  &:hover .button__text {
    color: transparent;
  }

  &:hover .button__icon {
    width: 148px;
    transform: translateX(0);
  }

  &:active .button__icon {
    background-color: #2e8644;
  }

  &:active {
    border: 1px solid #2e8644;
  }
`;

const ButtonText = styled.span`
  transform: translateX(30px);
  color: #fff;
  font-weight: 600;
  transition: all 0.3s;
`;

const ButtonIcon = styled.span`
  position: absolute;
  transform: translateX(109px);
  height: 100%;
  width: 39px;
  background-color: #34974d;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s;
`;

const SvgIcon = styled.svg`
  width: 30px;
  stroke: #fff;
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(0);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(0); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract}>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd}>+</Button>
      </QuantityControls>
      <AddToCartButton onClick={handleAddToCart}>
        <ButtonText className="button__text">AGREGAR</ButtonText>
        <ButtonIcon className="button__icon">
          <SvgIcon className="svg" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M5 12h14M12 5l7 7-7 7"/>
          </SvgIcon>
        </ButtonIcon>
      </AddToCartButton>
    </Container>
  );
};

export default ItemCount;
