// import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import PropsCallback from './ItemCount';
import { useState } from 'react';

function CardStyle({person}) {

  const [cartArticle, setCartArticle] = useState(0)

  return (
    <Card style={{ width: '19rem', padding: '0.5rem', margin: '0.4rem 0.4rem' }}>
      <Card.Img variant="top" src={person.image}></Card.Img> 
      <Card.Body>
        <Card.Title>{person.id}</Card.Title>
        <Card.Text>
        Talle: {person.size}<br></br> 
        Color: {person.color}<br></br>
        Precio: {person.price}
        </Card.Text>
        <PropsCallback setCartArticle={setCartArticle}/>
        </Card.Body>
    </Card>
  );
}

export default CardStyle;
