import React, { useState } from "react";
import styled from 'styled-components';

// Container for the whole component
const Container = styled.div`
  display: flex;
  flex-direction: column; /* Stack items vertically */
  align-items: center;
  border: 1px solid black;
  border-radius: 5px;
  padding: 1rem;
  background-color: white;
  width: 200px; /* Adjust width as needed */
`;

// Container for quantity controls
const QuantityControls = styled.div`
  display: flex;
  align-items: center;
  width: 100%; /* Full width of the container */
  justify-content: center; /* Center controls horizontally */
  margin-bottom: 1rem; /* Space between controls and add button */
`;

// Button styles for quantity control
const Button = styled.button`
  width: 3rem; /* Width of buttons */
  height: 3rem; /* Height of buttons */
  border: none; /* No border */
  background-color: #007BFF; /* Background color */
  cursor: pointer;
  color: white;
  font-size: 1.5rem; /* Larger font size */
  font-weight: bold; /* Bold font for emphasis */
  border-radius: 50%; /* Circular buttons */
  display: flex;
  justify-content: center;
  align-items: center;
  transition: background-color 0.2s ease;

  /* Ensure buttons have equal space */
  margin: 0; /* Remove margin for consistent spacing */

  &:hover {
    background-color: #0056b3;
  }

  &:active {
    background-color: #004085;
  }
`;

// Display for quantity value
const ValueDisplay = styled.span`
  width: 3rem; /* Width of value display */
  height: 3rem; /* Height of value display */
  text-align: center;
  background-color: white;
  color: orange;
  font-size: 1.5rem; /* Larger font size */
  font-weight: bold; /* Bold font for emphasis */
  border-radius: 50%; /* Circular display */
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0 0.5rem; /* Space between value and buttons */
`;

// Button style for adding to cart
const AddToCartButton = styled.button`
  width: 100%; /* Full width of the container */
  height: 45px; /* Height of the button */
  border-radius: 5px;
  border: none; /* No border */
  background-color: rgb(255, 208, 0);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition-duration: .5s;
  overflow: hidden;
  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.103);
  font-size: 1em; /* Font size to match card style */
  font-weight: 600;

  &:hover {
    background-color: rgb(255, 192, 0); /* Lighter background on hover */
  }

  &:active {
    transform: scale(0.95); /* Scale down on click */
  }
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(0);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(0); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract}>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd}>+</Button>
      </QuantityControls>
      <AddToCartButton onClick={handleAddToCart}>AGREGAR</AddToCartButton>
    </Container>
  );
};

export default ItemCount;
