import React, { useState } from "react";
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: space-between; /* Distribute space between items */
  align-items: center;
  border: 1px solid black;
  border-radius: 5px;
  padding: 0.5rem;
  background-color: white;
`;

const QuantityControls = styled.div`
  display: flex;
  align-items: center;
`;

const Button = styled.button`
  width: 2.5rem; /* Width of buttons */
  height: 2.5rem; /* Height of buttons */
  border: none;
  background-color: white;
  cursor: pointer;
  color: blue;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */

  &:first-child {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
  }

  &:last-child {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
  }
`;

const ValueDisplay = styled.span`
  width: 2.5rem; /* Width of value display */
  text-align: center;
  border: none;
  background-color: white;
  cursor: default;
  color: orange;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */
`;

const AddToCartButton = styled.button`
  background-color: lightblue;
  border: none;
  padding: 0.5rem 1rem;
  cursor: pointer;
  font-size: 0.9rem; /* Font size for the "Agregar" button */
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(1);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 1) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(1); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract}>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd}>+</Button>
      </QuantityControls>
      <AddToCartButton onClick={handleAddToCart}>AGREGAR</AddToCartButton>
    </Container>
  );
};

export default ItemCount;
