import React from 'react';
import Card from 'react-bootstrap/Card';
import styled from 'styled-components';
import ItemCount from './ItemCount';
import { Link } from 'react-router-dom';
import { useItem } from '../ItemContext';

// Estilos para el contenedor de la imagen
const ImageContainer = styled.div`
  overflow: hidden; /* Ocultar el desbordamiento */
  border-radius: 5px; /* Opcional: para redondear las esquinas */
`;

// Estilos para la imagen con efecto de zoom
const ZoomImage = styled(Card.Img)`
  transition: transform 0.3s ease-in-out; /* Transición suave */

  &:hover {
    transform: scale(1.2); /* Escalar la imagen al pasar el puntero */
  }
`;

// Estilos base para los botones
const BaseButton = styled.button`
  display: inline-block;
  width: 100%;
  margin-top: 10px;
  padding: 5px 10px;
  color: white;
  background-color: orange;
  border: none;
  border-radius: 5px;
  text-decoration: none;
  text-align: center;
  cursor: pointer;

  &:hover {
    background-color: darkorange;
  }
`;

// Estilo para el botón "Ver más"
const MoreButton = styled(Link)`
  ${BaseButton} /* Aplicar estilos base */
`;

// Estilo para el botón "Agregar"
const AddButton = styled(BaseButton)`
  margin-top: 10px;
`;

function CardStyle({ person, addToCart }) {
  const { setSelectedItem } = useItem();

  const handleAddToCart = (quantity) => {
    addToCart(person, quantity);
  };

  const handleMoreClick = () => {
    setSelectedItem(person);
  };

  return (
    <Card style={{ width: '19rem', padding: '0.5rem', margin: '0.4rem 0.4rem' }}>
      <ImageContainer>
        <ZoomImage variant="top" src={person.image} alt={person.name} />
      </ImageContainer>
      <Card.Body>
        <Card.Title>{person.name}</Card.Title>
        <Card.Text>
          Talle: {person.size}<br />
          Color: {person.color}<br />
          Precio: {person.price}
        </Card.Text>
        <ItemCount setCartArticle={handleAddToCart} />
        <MoreButton to={`/detail/${person.id}`} onClick={handleMoreClick}>Ver más</MoreButton>
        <AddButton onClick={() => handleAddToCart(1)}>Agregar</AddButton>
      </Card.Body>
    </Card>
  );
}

export default CardStyle;
