import { width } from "@fortawesome/free-solid-svg-icons/fa0";
import React, { useState, useRef } from "react";

const ChildCallback = ({ onClick, text }) => (
  <button onClick={onClick}>{text}</button>
);

const PropsCallback = () => {
  const [value, setValue] = useState(0);
  const valueRef = useRef(0);

  const handleRefAdd = () => {
    valueRef.current++;
    setValue(valueRef.current);
  };

  const handleRefSubtract = () => {
    if(valueRef.current > 0)
    valueRef.current--;
    setValue(valueRef.current);
  };

  return (
    <div style={{
        display : 'flex'
    }}>
      <ChildCallback onClick={handleRefSubtract} text='-' 
    style={{
        width: '1rem'
    }}
      />
      <span>{value}</span>
      <ChildCallback onClick={handleRefAdd} text='+' />
    <div>
        <ChildCallback onclick={ChildCallback} text= 'Agregar' />
    </div>
    </div>

  );
};

export default PropsCallback;
