import React, { useState, useEffect } from 'react';
import './App.css';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer';
import {CardList} from './components/CardList';
import PropsCallback from './components/PropsCallback';

function App() {
  const [cartArticle, setCartArticle] = useState([]);
  const [Datas, setDatas] = useState([]);
  const [Loading, setLoading] = useState(true);
  const [cartTotal, setCartTotal] = useState(0);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 3000);

    fetch('https://66ca0d49f009b9d5c730fd4f.mockapi.io/stock')
      .then(response => response.json())
      .then(data => setDatas(data))
      .catch(error => {
        console.error('Error fetching data:', error);
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <NavBar cartArticle={cartArticle} cartTotal={cartTotal} />
      <ItemListContainer greetings="Bienvenidos a" greetings1="STADIUM" />
      <CardList Datas={Datas} Loading={Loading} setCartArticle={setCartArticle} setCartTotal={setCartTotal}/>
      {Datas.map(item => (
        <PropsCallback 
          key={item.id} 
          id={item.id} 
          setCartTotal={setCartTotal} 
          stock={item.stock} 
        />
      ))}
    </div>
  );
}

export default App;
