import React, { useState, useRef } from "react";

// Define el componente ChildCallback con estilo en línea
const ChildCallback = ({ onClick, text, style }) => (
  <button onClick={onClick} style={style}>
    {text}
  </button>
);

const PropsCallback = () => {
  const [value, setValue] = useState(0); // Estado para mostrar el valor
  const valueRef = useRef(0); // Referencia para almacenar el valor actual

  const handleRefAdd = () => {
    valueRef.current++;
    setValue(valueRef.current); // Actualiza el valor mostrado
  };

  const handleRefSubtract = () => {
    // Solo decrementa si el valor actual es mayor que 0
    if (valueRef.current > 0) {
      valueRef.current--;
      setValue(valueRef.current); // Actualiza el valor mostrado
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <ChildCallback 
        onClick={handleRefSubtract} 
        text='-' 
        style={{ width: '2rem', marginRight: '1rem' }} 
      />
      <span style={{ margin: '0 1rem' }}>{value}</span>
      <ChildCallback 
        onClick={handleRefAdd} 
        text='+' 
        style={{ width: '2rem', marginLeft: '1rem' }} 
      />
      <div style={{ marginLeft: '1rem' }}>
        <ChildCallback 
          onClick={() => alert('Agregar')} 
          text='Agregar' 
          style={{ backgroundColor: 'lightblue', border: 'none', padding: '0.5rem 1rem' }} 
        />
      </div>
    </div>
  );
};

export default PropsCallback;
