import React, { useState } from "react";
import styled from 'styled-components';

// Container for the whole component
const Container = styled.div`
  display: flex;
  justify-content: space-between; /* Distribute space between items */
  align-items: center;
  border: 1px solid black;
  border-radius: 5px;
  padding: 0.5rem;
  background-color: white;
`;

// Container for quantity controls
const QuantityControls = styled.div`
  display: flex;
  align-items: center;
`;

// Button styles for quantity control
const Button = styled.button`
  width: 2.5rem; /* Width of buttons */
  height: 2.5rem; /* Height of buttons */
  border: none;
  background-color: white;
  cursor: pointer;
  color: blue;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */

  &:first-child {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
  }

  &:last-child {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
  }
`;

// Display for quantity value
const ValueDisplay = styled.span`
  width: 2.5rem; /* Width of value display */
  text-align: center;
  border: none;
  background-color: white;
  cursor: default;
  color: orange;
  font-size: 1.2rem; /* Smaller font size */
  font-weight: bold; /* Bold font for emphasis */
`;

// Button style for adding to cart
const AddToCartButton = styled.button`
  min-width: 100px; /* Minimum width */
  height: 45px; /* Height of the button */
  padding: 8px;
  border-radius: 5px; /* Rounded corners */
  border: 2.5px solid #E0E1E4; /* Border style */
  box-shadow: 0px 0px 20px -20px; /* Shadow effect */
  cursor: pointer;
  background-color: white;
  transition: all 0.2s ease-in-out; /* Smooth transition */
  font-family: 'Poppins', sans-serif; /* Font family */
  font-size: 0.9rem; /* Font size */

  &:hover {
    background-color: #F2F2F2; /* Lighter background on hover */
    box-shadow: 0px 0px 20px -18px; /* Slightly larger shadow on hover */
  }

  &:active {
    transform: scale(0.95); /* Scale down on click */
  }
`;

const ItemCount = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(0);

  const handleRefAdd = () => {
    if (value < stock) {
      setValue(value + 1);
    }
  };

  const handleRefSubtract = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(0); // Reset value after adding to cart
  };

  return (
    <Container>
      <QuantityControls>
        <Button onClick={handleRefSubtract}>-</Button>
        <ValueDisplay>{value}</ValueDisplay>
        <Button onClick={handleRefAdd}>+</Button>
      </QuantityControls>
      <AddToCartButton onClick={handleAddToCart}>AGREGAR</AddToCartButton>
    </Container>
  );
};

export default ItemCount;
