import React from 'react';
import styled from 'styled-components';

const LoaderContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform-style: preserve-3d;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh; /* Ensure the loader takes up full height */
`;

const Loop = styled.div`
  transform: rotateX(-35deg) rotateY(-45deg) translateZ(1.5625em);
`;

const Item = styled.div`
  margin: -1.5625em;
  width: 3.125em;
  height: 3.125em;
  transform-origin: 50% 50% -1.5625em;
  box-shadow: 0 0 0.125em currentColor;
  background: currentColor;
  animation: s 0.6s cubic-bezier(0.45, 0.03, 0.51, 0.95) infinite alternate;

  &:before,
  &:after {
    position: absolute;
    width: inherit;
    height: inherit;
    transform-origin: 0 100%;
    box-shadow: inherit;
    background: currentColor;
    content: "";
  }

  &:before {
    bottom: 100%;
    transform: rotateX(90deg);
  }

  &:after {
    left: 100%;
    transform: rotateY(90deg);
  }

  /* Define colors and animation delays for each item */
  &:nth-child(1) {
    margin-top: 6.25em;
    color: #fe1e52;
    animation-delay: -1.2s;
  }

  &:nth-child(1):before {
    color: #ff6488;
  }

  &:nth-child(1):after {
    color: #ff416d;
  }

  &:nth-child(2) {
    margin-top: 3.125em;
    color: #fe4252;
    animation-delay: -1s;
  }

  &:nth-child(2):before {
    color: #ff8892;
  }

  &:nth-child(2):after {
    color: #ff6572;
  }

  &:nth-child(3) {
    margin-top: 0em;
    color: #fe6553;
    animation-delay: -0.8s;
  }

  &:nth-child(3):before {
    color: #ffa499;
  }

  &:nth-child(3):after {
    color: #ff8476;
  }

  &:nth-child(4) {
    margin-top: -3.125em;
    color: #fe8953;
    animation-delay: -0.6s;
  }

  &:nth-child(4):before {
    color: #ffb999;
  }

  &:nth-child(4):after {
    color: #ffa176;
  }

  &:nth-child(5) {
    margin-top: -6.25em;
    color: #feac54;
    animation-delay: -0.4s;
  }

  &:nth-child(5):before {
    color: #ffce9a;
  }

  &:nth-child(5):after {
    color: #ffbd77;
  }

  &:nth-child(6) {
    margin-top: -9.375em;
    color: #fed054;
    animation-delay: -0.2s;
  }

  &:nth-child(6):before {
    color: #ffe49a;
  }

  &:nth-child(6):after {
    color: #ffda77;
  }
`;

const Loader = () => (
  <LoaderContainer>
    <Loop>
      {[...Array(6)].map((_, index) => (
        <Item key={index} />
      ))}
    </Loop>
  </LoaderContainer>
);

export default Loader;
