import React, { useState, useRef, useEffect } from "react";
import styled from 'styled-components';

// Styled-components for styling
const CounterContainer = styled.div`
  display: flex;
  align-items: center;
  border: 1px solid black;
  border-radius: 5px;
`;

const Button = styled.button`
  width: 2rem;
  border: none;
  background-color: white;
  cursor: pointer;
  color: blue;
`;

const AddToCartButton = styled.button`
  background-color: lightblue;
  border: none;
  cursor: pointer;
  display; flex;
  align
`;

// Component implementation
const PropsCallback = ({ setCartArticle }) => {
  const stock = 15;
  const [value, setValue] = useState(1);
  const valueRef = useRef(1); // Use ref to keep the value updated

  const handleRefAdd = () => {
    if (valueRef.current < stock) {
      valueRef.current++;
      setValue(valueRef.current);
    }
  };

  const handleRefSubtract = () => {
    if (valueRef.current > 1) {
      valueRef.current--;
      setValue(valueRef.current);
    }
  };

  const handleAddToCart = () => {
    setCartArticle(value);
    setValue(1); // Reset value after adding to cart
    valueRef.current = 1; // Reset ref value
  };

  return (
    <CounterContainer>
      <Button onClick={handleRefSubtract}>-</Button>
      <ValueDisplay>{value}</ValueDisplay>
      <Button onClick={handleRefAdd}>+</Button>
      <AddToCartButton onClick={handleAddToCart}>Agregar</AddToCartButton>
    </CounterContainer>
  );
};

export default PropsCallback;
