import React from 'react';
import Card from 'react-bootstrap/Card';
import PropsCallback from './ItemCount'; // Asegúrate de que el nombre del archivo sea correcto

function CardStyle({ person, addToCart }) {
  const handleAddToCart = (quantity) => {
    addToCart(person, quantity);
  };

  return (
    <Card style={{ width: '19rem', padding: '0.5rem', margin: '0.4rem 0.4rem' }}>
      <Card.Img variant="top" src={person.image} alt={person.name} />
      <Card.Body>
        <Card.Title>{person.name}</Card.Title>
        <Card.Text>
          Talle: {person.size}<br />
          Color: {person.color}<br />
          Precio: {person.price}
        </Card.Text>
        <PropsCallback setCartArticle={handleAddToCart} />
      </Card.Body>
    </Card>
  );
}

export default CardStyle;
